#include <string>
#include <iostream>
#include <algorithm>
using namespace std;
class Solution
{
public:
    string longestPalindrome(string s)
    {
        string s_backward = s;
        reverse(s_backward.begin(), s_backward.end());
        if (s == s_backward)
        {
            return s;
        }

        string longest_palidrome = s.substr(0, 1);
        for (int i = 0; i < s.length(); i++)
        {
            for (int j = s.length(); j > i; j--)
            {
                if (s[i] == s[j])
                {
                    string forward = s.substr(i, j - i + 1);
                    string backward = forward;
                    reverse(backward.begin(), backward.end());
                    if (forward == backward)
                    {
                        if (forward.length() > longest_palidrome.length())
                        {
                            longest_palidrome = forward;
                        }
                    }
                }
            }
        }
        return longest_palidrome;
    }
};

int main()
{
    Solution s;
    string str = "";
    string result = s.longestPalindrome(str);
    cout << result << endl;
    return 0;
}